(function() {
  var CSON;

  CSON = require("../README");

  describe("CSON", function() {
    it("should parse", function() {
      var result;
      result = CSON.parse("hello: \"duder\"");
      assert(result.hello);
      return assert.equal(result.hello, "duder");
    });
    return it("should allow comments", function() {
      var result;
      result = CSON.parse("# Some comment\nhey: \"yolo\" # Fo 'sho!");
      return assert.equal(result.hey, "yolo");
    });
  });

}).call(this);

